mapper.select:
SELECT FORMAT(boardNo, 0) AS boardNo , boardSeq , boardTitle , boardText , writeUserId , writeUserName , createDt , updateDt FROM ( SELECT * FROM ( SELECT (@ROWNUM := @ROWNUM + 1) AS boardNo , board_seq AS boardSeq , board_title AS boardTitle , board_text AS boardText , write_user_id AS writeUserId , write_user_name AS writeUserName , DATE_FORMAT(create_dt,'%Y-%m-%d %H:%i:%s') AS createDt , DATE_FORMAT(update_dt,'%Y-%m-%d %H:%i:%s') AS updateDt FROM TB_BOARD, (SELECT @ROWNUM := 0) AS R
board_title like CONCAT('%', #{searchValue}, '%')
board_text like CONCAT('%', #{searchValue}, '%')
write_user_name = #{searchValue}
) AS DATA ORDER BY boardNo DESC ) AS RESULT LIMIT #{pagePerCount} OFFSET #{offset}

mapper.insert:
INSERT INTO TB_BOARD
(
	board_title
	, board_text
	, write_user_id
	, write_user_name
	, create_dt
	, update_dt
) VALUES (
	#{boardTitle}
	, #{boardText}
	, #{writeUserId}
	, #{writeUserName}
	, now()
	, now()
)

mapper.update:
UPDATE TB_BOARD


		board_title = #{boardTitle},


		board_text = #{boardText},

	update_dt = now()

WHERE board_seq = #{boardSeq}
AND	  write_user_id = #{writeUserId}

mapper.delete:
DELETE FROM TB_BOARD
WHERE board_seq = #{boardSeq}
AND write_user_id = #{writeUserId}
